//https://angular.io/guide/testing-components-basics

import { TestBed } from "@angular/core/testing";
import { WelcomeComponent } from "./welcome.component";
import { UserService } from "../../model/user.service";

class MockUserService {
    isLoggedIn = true;
    user = { name: 'Test User' };
  }

  let comp: WelcomeComponent | undefined = undefined;
 let userService: UserService | undefined = undefined;

  describe('WelcomeComponent',()=>{
    beforeEach(() => {
    TestBed.configureTestingModule({
      // provide the component-under-test and dependent service
      providers: [WelcomeComponent, { provide: UserService, useClass: MockUserService }],
    });
    // inject both the component and the dependent service.
     comp = TestBed.inject(WelcomeComponent);
     userService = TestBed.inject(UserService);
  });

  it('should not have welcome message after construction', () => {
    if(comp)
       expect(comp.welcome).toBe('');
  });

  it('should welcome logged in user after Angular calls ngOnInit', () => {
    if(comp && userService)
    {
    comp.ngOnInit();
    expect(comp.welcome).toContain(userService.user.name);
    }
  });

  it('should ask user to log in if not logged in after ngOnInit', () => {
    if(comp && userService)
    {
    userService.isLoggedIn = false;
    comp.ngOnInit();
    expect(comp.welcome).not.toContain(userService.user.name);
    expect(comp.welcome).toContain('log in');
    }
  });
});
